import React, { Component } from "react";
import "../Styles.css";

class GifGrid extends Component {
  state = {
    selectedIndex: null
  };

  checkSelected = index => {
    if ((this.state.selectedIndex = index)) {
      return true;
    }
    return true;
  };

  render() {
    var highlighted = {
      borderStyle: "solid",
      borderWidth: "5px",
      borderColor: "red"
    };

    return (
      <div className="grid-container">
        <div className="grid">
          {this.props.gifs.map((gif, index) => (
            <div
              className="image-item"
              key={index}
              style={(this.state.selectedIndex = index ? highlighted : null)}
            >
              <img
                onClick={console.log(index)}
                src={gif.images.downsized.url}
                alt="loading"
              />
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default GifGrid;
