import React from "react";
import "./Styles.css";
import { PopupWindow, Header } from "./components";
import { Button } from "react-bootstrap";

class App extends React.Component {
  state = {
    gifs: [],
    offset: 0,
    currentGif: "",
    searchInput: "",
    timeout: 0,
    selectedGif: "",
    showModal: false
  };

  fetchData = (keyWord, limit, offset) => {
    this.setState({ currentGif: keyWord });
    const url = `http://api.giphy.com/v1/gifs/search?api_key=smFg8BlXGx3Fv1uibDi6kK0MiFnLr1xx&q=${keyWord}&offset=${offset}&limit=${limit}`;
    console.log(url);
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.setState({
          gifs: this.state.gifs.concat(data.data),
          offset: offset + limit
        });
      });
  };

  handleInputChange = event => {
    var searchText = event.target.value;
    if (this.timeout) clearTimeout(this.timeout);
    this.timeout = setTimeout(() => {
      this.setState({ gifs: [] });
      this.fetchData(searchText, 9, 0);
    }, 500);
  };

  selectGif = gif => {
    try {
      const url = gif.images.downsized.url;
      console.log(url);
      this.setState({ selectedGif: url });
    } catch (err) {
      document.getElementById("demo").innerHTML = err.name;
    }
  };

  showModal = () => {
    this.setState({ showModal: true });
  };

  hideModal = () => {
    this.setState({ showModal: false });
  };

  render() {
    return (
      <div>
        <PopupWindow
          state={this.state}
          showModal={this.showModal}
          hideModal={this.hideModal}
          handleInputChange={this.handleInputChange}
          selectGif={this.selectGif}
        />
        <Header title="GIF Picker" />
        <Button onClick={this.showModal}> Press me! </Button>
        <div className="selected_gif">
          <img src={this.state.selectedGif} alt="loading" />
        </div>
      </div>
    );
  }
}

export default App;
