import React from "react";
import "./Styles.css";
import { PopupWindow, Header } from "./components";
import { Button } from "react-bootstrap";

class App extends React.Component {
  state = {
    showModal: false
  };

  showModal = () => {
    this.setState({ showModal: true });
  };

  hideModal = () => {
    this.setState({ showModal: false });
    this.setState({ gifs: [] });
  };

  render() {
    return (
      <div>
        <PopupWindow
          state={this.state}
          showModal={this.showModal}
          hideModal={this.hideModal}
          handleInputChange={this.handleInputChange}
          selectGif={this.selectGif}
          fetchData={this.fetchData}
        />
        <Header title="GIF Picker" />
        <Button onClick={this.showModal}> Press me! </Button>
        <div className="selected_gif">
          <img src={this.state.selectedGif} alt="loading" />
        </div>
      </div>
    );
  }
}

export default App;
