import React, { Component } from "react";
import "../Styles.css";

class GifGrid extends Component {
  state = {
    selectedIndex: nul
  };

  checkSelected = index => {
    if ((this.state.selectedIndex = index)) {
      return true;
    }
    return false;
  };

  render() {
    var highlighted = {
      borderStyle: "solid",
      borderWidth: "5px",
      borderColor: "red"
    };
    console.log(this.state.selectedIndex);

    return (
      <div className="grid-container">
        <div className="grid">
          {this.props.gifs.map((gif, index) => (
            <div
              className="image-item"
              key={index}
              onClick={index => this.setState({ selectedIndex: index })}
              style={this.checkSelected ? highlighted : null}
            >
              <img src={gif.images.downsized.url} alt="loading" />
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default GifGrid;
