import React from "react";
import { Modal, Button } from "react-bootstrap";
import { GifGrid, SearchBar, Header } from ".";
import InfiniteScroll from "react-infinite-scroll-component";

const PopupWindow = props => {
  const { gifs, currentGif, showModal } = props.state;
  return (
    <Modal show={props.modalState} onHide={props.hideModal}>
      <Modal.Header closeButton>
        <Modal.Title>Modal heading</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div>
          <SearchBar inputHandler={props.handleInputChange} />
          <InfiniteScroll
            dataLength={gifs.length}
            next={() => {
              const { offset } = props.state;
              this.fetchData(currentGif, 6, offset);
            }}
            hasMore={true}
            scrollThreashold={0.5}
          >
            <GifGrid gifs={gifs} selectGif={props.selectGif} />
          </InfiniteScroll>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={props.hideModal}>
          Close
        </Button>
        <Button variant="primary" onClick={props.hideModal}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default PopupWindow;
