import React, { Component } from "react";
import "../Styles.css";

class GifGrid extends Component {
  state = {
    selectedIndex: null
  };

  handleClick() {
    document.getElementById("itemid").className = "selected_item";
    console.log(document.getElementById("itemid"));
  }

  render() {
    var highlighted = {
      borderStyle: "solid",
      borderWidth: "5px",
      borderColor: "red"
    };
    return (
      <div className="grid-container">
        <div className="grid">
          {this.props.gifs.map((gif, index) => (
            <div
              id="itemid"
              className="image-item"
              key={index}
              onClick={this.handleClick}
              className=""
            >
              <img src={gif.images.downsized.url} alt="loading" />
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default GifGrid;
