import React from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import "./Styles.css";
import Header from "./components/Header";
import GifGrid from "./components/GifGrid";

class App extends React.Component {
  state = {
    gifs: [],
    offset: 0,
    currentGif: "",
    searchInput: "",
    timeout: 0,
    selectedGif: ""
  };

  fetchData = (keyWord, limit, offset) => {
    this.setState({ currentGif: keyWord });
    const url = `http://api.giphy.com/v1/gifs/search?api_key=smFg8BlXGx3Fv1uibDi6kK0MiFnLr1xx&q=${keyWord}&offset=${offset}&limit=${limit}`;
    console.log(url);
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.setState({
          gifs: this.state.gifs.concat(data.data),
          offset: offset + limit
        });
      });
  };

  handleSubmit = event => {
    event.preventDefault();
    console.log("hello");
  };

  handleInputChange = event => {
    var searchText = event.target.value;
    if (this.timeout) clearTimeout(this.timeout);
    this.timeout = setTimeout(() => {
      this.setState({ gifs: [] });
      this.fetchData(searchText, 9, 0);
    }, 500);
  };

  selectGif = gif => {
    this.setState({ selectedGif: gif });
    console.log(gif);
  };

  render() {
    const { currentGif, gifs, searchInput } = this.state;
    return (
      <div>
        <Header title="GIF Picker" />
        <form onSubmit={this.handleSubmit}>
          <p>
            <input
              type="text"
              placeholder="Serach GIF..."
              onChange={this.handleInputChange}
              name="searchInput"
            />
          </p>
        </form>
        <div className="page_style">
          <InfiniteScroll
            dataLength={gifs.length}
            next={() => {
              const { offset } = this.state;
              this.fetchData(currentGif, 6, offset);
            }}
            hasMore={true}
            scrollThreashold={0.5}
          >
            <GifGrid gifs={gifs} selectGif={this.selectGif} />
          </InfiniteScroll>
          <div className="selected_gif">
            <img src={this.state.selectedGif} alt="loading" />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
