import React from "react";
import { Modal, Button } from "react-bootstrap";
import { GifGrid, SearchBar } from ".";
import InfiniteScroll from "react-infinite-scroll-component";
import "../Styles.css";

const PopupWindow = props => {
  const { gifs, currentGif } = props.state;
  return (
    <Modal show={props.state.showModal} onHide={props.hideModal}>
      <Modal.Header closeButton>
        <Modal.Title>GIF Picker</Modal.Title>
      </Modal.Header>
      <Modal.Body
        id="scrollableDiv"
        style={{
          "max-height": "calc(100vh - 210px)",
          "overflow-y": "auto"
        }}
      >
        <div>
          <SearchBar inputHandler={props.handleInputChange} />
          <InfiniteScroll
            dataLength={gifs.length}
            next={() => {
              console.log("hello");
              const { offset } = props.state;
              props.fetchData(currentGif, 6, offset);
            }}
            hasMore={true}
            scrollThreashold={0.5}
            scrollableTarget="scrollableDiv"
          >
            <GifGrid gifs={gifs} selectGif={props.selectGif} />
          </InfiniteScroll>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={props.hideModal}>
          Close
        </Button>
        <Button variant="primary" onClick={props.hideModal}>
          Select GIF
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default PopupWindow;
